html, body, #root {
  margin: 0;
  padding: 0;
  height: 100%;
  width: 100%;
}

*, *::before, *::after {
  box-sizing: border-box;
}

.main-parent {
  display: flex;
  flex-direction: row; /* Change to row for horizontal layout */
  min-height: 100vh; /* Ensure it stretches to fit content */
  width: 100%;
}

.left-bar {
  width: 250px; /* Adjust the width as needed */
  background-color: #2c3e50; /* Example background color */
  padding: 20px;
  box-shadow: 2px 0 5px rgba(0, 0, 0, 0.1);
  height: 100vh; /* Ensure it stretches to fit the viewport height */
  position: fixed; /* Make the left bar fixed */
  top: 0;
  left: 0;
}

.main-interface {
  margin-left: 250px; /* Add margin to account for the fixed left bar */
  flex: 1;
  background-color: #314a57;
  padding: 0px;
  display: flex;
  flex-direction: column;
  align-items: center;
  overflow-y: auto; /* Allow scrolling if content overflows */
}
