.ImportantMessages {
    width: 100%;
    height: 100%;
    padding: 128px 225px 0 224px;
    background: #243541;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  
  .LoginPadding {
    width: 100%;
    padding: 543px 853px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 10px;
  }
  
  .LoginSection {
    width: 100%;
    height: auto;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    gap: 10px;
  }
  
  .WelcomeText {
    color: white;
    font-size: 150px;
    font-family: 'JetBrains Mono', monospace;
    font-weight: 700;
  }
  
  .Frame34 {
    padding: 62px 53px;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: flex-start;
    gap: 10px;
  }
  
  .EmailColumn,
  .PasswordColumn {
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: flex-start;
    gap: 12px;
  }
  
  .EmailAddress,
  .Password {
    color: white;
    font-size: 70px;
    font-family: 'JetBrains Mono', monospace;
    font-weight: 700;
  }
  
  .InputField {
    width: 1009px;
    height: 112px;
    background: #d9d9d9;
    border: none;
    padding: 10px;
    font-size: 20px;
  }
  
  .SubmitSection {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 87px 0;
  }
  
  .SubmitButton {
    width: 1009px;
    height: 112px;
    background: #314a57;
    color: white;
    font-size: 70px;
    font-family: 'JetBrains Mono', monospace;
    font-weight: 700;
    border: none;
    cursor: pointer;
  }
  
  .ErrorMessage {
    color: red;
    font-size: 16px;
    margin-top: 20px;
  }
